import{a as u}from"./chunk-3MEYZEWF.js";import{a as l,b as m,c as g,d as h}from"./chunk-BIGKW4PL.js";import{Fa as a,Ga as p,Ha as c,Ia as r,Va as t,Y as n,la as i,ta as d}from"./chunk-UNDSUC2M.js";var w=class s{VideoFormat=l;contactText=n("Public Repository Link");titleOne=n("Games");headerOne=n("Unity/node.js/.NET/MongoDB (C#/TS) - Gwent Clone (Indefinitely Postponed)");introOne=n("This was a fullstack set up I was working on because, at the time, most of my main friend group and I were playing through the Witcher 3 and I wanted to make a multiplayer version of the minigame Gwent. I figured it would be fun if we all played Gwent against each other. The game was going to be downloadable via a launcher. I was going to make a website that could track silly user statistics. Finally, I had plans to expand on the base Gwent game with new mechanics and cards. I ended up stopping the project because it could never be deployed on Steam and because I enjoyed using Unreal Engine's multiplayer framework much more than Unity's.");imgOne=n("I was getting tired with using Unity Netcode. When I started implementing more and more mechanics from Gwent, I felt like I was getting too complacent with the code architecture and was starting to do things in too sloppy of a manner. At the very least, when I had stopped working on this project, +99% of everything that I had implemented was working correctly and without the burnout could have very reasonably been completed.");contactTypeOne=n("Game Client");contactLinkOne=n("https://github.com/wmsauve/GwentClone");imgTwo=n("Simple launcher. Over time, there were plans to improve this application to show more stuff. From what I remember, it correctly handled downloading the game client and launching the game if detected in a default install location on your device.");contactTypeTwo=n("Game Launcher");contactLinkTwo=n("https://github.com/wmsauve/GwentLauncher");contactTypeThree=n("Game Server");contactLinkThree=n("https://github.com/wmsauve/GwentServer");textBodyOne=n("The server was a simple express server. From what I remember, creating a new user account and logging in, saving your deck selections, and loading saved decks were all working. As mentioned above, I was planning to collect silly statistics about matchups and deck/card usage rates. Regardless of whether this was something my friends cared all that much about over time, it was a useful exercise in learning how to set up a database.");titleTwo=n("Miscellaneous Projects");static \u0275fac=function(o){return new(o||s)};static \u0275cmp=d({type:s,selectors:[["app-personal"]],decls:10,vars:38,consts:[[3,"headerText"],[3,"headerText","introText"],[1,"dynamicGrid-2x1"],["imageSrc","personalgame1.jpg",3,"imageHeight","imageWidth","textContent","textPos"],["imageSrc","personalgame2.jpg",3,"imageHeight","imageWidth","textContent","textPos"],[3,"introText"],[3,"firstText","secondText","secondLink"]],template:function(o,e){o&1&&(r(0,"app-std-header-text",0)(1,"app-std-header-intro-text",1),p(2,"div",2),r(3,"app-stdimg",3)(4,"app-stdimg",4),c(),r(5,"app-std-header-intro-text",5)(6,"app-list-of-links",6)(7,"app-list-of-links",6)(8,"app-list-of-links",6)(9,"app-std-header-text",0)),o&2&&(a("headerText",t(e.titleOne())),i(),a("headerText",t(e.headerOne()))("introText",t(e.introOne())),i(2),a("textContent",t(e.imgOne()))("imageHeight",289)("imageWidth",600)("textPos",e.VideoFormat.Right),i(),a("textContent",t(e.imgTwo()))("imageHeight",320)("imageWidth",600)("textPos",e.VideoFormat.Left),i(),a("introText",t(e.textBodyOne())),i(),a("firstText",t(e.contactTypeOne()))("secondText",t(e.contactText()))("secondLink",t(e.contactLinkOne())),i(),a("firstText",t(e.contactTypeTwo()))("secondText",t(e.contactText()))("secondLink",t(e.contactLinkTwo())),i(),a("firstText",t(e.contactTypeThree()))("secondText",t(e.contactText()))("secondLink",t(e.contactLinkThree())),i(),a("headerText",t(e.titleTwo())))},dependencies:[h,m,g,u],encapsulation:2})};export{w as Personal};
